prompt.output_format:
Return ONLY valid JSON in this exact structure:
{{
  "topics_and_themes": ["main topic 1", "theme 2", "..."],
  "writing_style_and_quality": {{
    "style": "professional|casual|conversational|formal|creative|technical",
    "quality": "low|medium|high|excellent",
    "tone": "authoritative|friendly|inspirational|humorous|informative|persuasive",
    "readability": "easy|moderate|complex"
  }},
  "sentiment_analysis": {{
    "overall_sentiment": "positive|neutral|negative|mixed",
    "emotional_tone": "description of emotional quality",
    "mood": "enthusiastic|calm|urgent|reflective|passionate|etc"
  }},
  "content_category": "business|tech|career|education|lifestyle|marketing|leadership|innovation|news|opinion|personal|entertainment|other",
  "engagement_potential": {{
    "hook_strength": 0-100,
    "opening_line": "the first sentence or opening of the post",
    "attention_grabbing": "yes|no|somewhat",
    "shareability": 0-100,
    "comment_likelihood": 0-100
  }},
  "ctas_present": {{
    "has_cta": true|false,
    "cta_type": "like|share|comment|follow|click_link|apply|register|learn_more|none",
    "cta_description": "description of CTA if present, otherwise null"
  }},
  "key_points_and_takeaways": {{
    "main_message": "core message of the post",
    "key_points": ["point 1", "point 2", "..."],
    "value_proposition": "what value does this provide to readers"
  }},
  "target_audience": {{
    "primary_audience": "description of who this is for",
    "professional_level": "entry|mid|senior|executive|all",
    "industry_relevance": ["industry 1", "industry 2", "..."]
  }},
  "content_structure": {{
    "has_hashtags": true|false,
    "hashtags": ["hashtag1", "hashtag2", "..."] or null,
    "has_mentions": true|false,
    "has_links": true|false,
    "length_category": "short|medium|long",
    "formatting": "plain|paragraphs|bullet_points|mixed"
  }}
}}

