  "visual_style_and_quality": {{
    "style": "description of visual aesthetic",
    "quality": "low|medium|high|professional",
    "colors": ["dominant color 1", "color 2"],
    "production_notes": "brief description"
  }},
  "audio_description": {{
    "speech_present": true|false,
    "speech_description": "description if present, otherwise null",
    "music_present": true|false,
    "music_description": "description if present, otherwise null",
    "overall_audio_quality": "low|medium|high"
  }},
  "transcript": "full transcript if speech present, otherwise null",
  "mood_and_tone": {{
    "mood": "energetic|calm|dramatic|humorous|serious|inspirational|etc",
    "emotional_tone": "description of emotional quality",
    "vibe": "overall feeling/atmosphere"
  }},
  "content_category": "comedy|educational|lifestyle|fashion|tech|food|travel|fitness|entertainment|music|dance|tutorial|review|vlog|other",
  "hook_effectiveness": {{
    "first_3_seconds": "description of what happens in first 3 seconds",
    "hook_strength": 0-100,
    "attention_grabbing": "yes|no|somewhat",
    "hook_type": "visual|text|audio|action|question|statement|other"
